/* Demo theme configuration for Tabler */
(function () {
    'use strict';
    
    // Theme management
    var theme = localStorage.getItem('tablerTheme') || 'dark';
    
    function applyTheme(themeName) {
        document.body.setAttribute('data-bs-theme', themeName);
        document.documentElement.setAttribute('data-bs-theme', themeName);
        
        if (themeName === 'dark') {
            document.body.classList.add('theme-dark');
            document.body.classList.remove('theme-light');
        } else {
            document.body.classList.add('theme-light');
            document.body.classList.remove('theme-dark');
        }
        
        localStorage.setItem('tablerTheme', themeName);
    }
    
    // Apply theme immediately to prevent flash
    applyTheme(theme);
    
    // Theme toggle functionality (if needed)
    document.addEventListener('DOMContentLoaded', function() {
        var themeToggle = document.querySelector('[data-bs-theme-value]');
        
        if (themeToggle) {
            themeToggle.addEventListener('click', function() {
                var currentTheme = localStorage.getItem('tablerTheme') || 'dark';
                var newTheme = currentTheme === 'dark' ? 'light' : 'dark';
                applyTheme(newTheme);
            });
        }
    });
})();